http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "eventName"
        ],
        "type": "object",
        "properties": {
          "occurredAt": {
            "type": "string",
            "description": "The time when this event occurred. If this isn't set, the current time will be used.",
            "format": "date-time"
          },
          "eventName": {
            "type": "string",
            "description": "The internal name of the event (`pe<portalID>_eventName`). Can be retrieved through the [event definitions API](https://developers.hubspot.com/docs/reference/api/analytics-and-events/custom-events/custom-event-definitions#get-%2Fevents%2Fv3%2Fevent-definitions) or in [HubSpot's UI](https://knowledge.hubspot.com/reports/create-custom-behavioral-events-with-the-code-wizard#find-internal-name). "
          },
          "utk": {
            "type": "string",
            "description": "The visitor's usertoken. Used for associating the event data with a CRM record."
          },
          "uuid": {
            "type": "string",
            "description": "Include a universally unique identifier to assign a unique ID to the event completion. Can be useful for matching data between HubSpot and other external systems."
          },
          "email": {
            "type": "string",
            "description": "The visitor's email address. Used for associating the event data with a CRM record."
          },
          "properties": {
            "type": "object",
            "additionalProperties": {
              "type": "string"
            },
            "description": "The event properties to update. Takes the format of key-value pairs (property internal name and property value). Learn more about [HubSpot's default event properties](https://developers.hubspot.com/docs/guides/api/analytics-and-events/custom-events/custom-event-definitions#hubspot-s-default-event-properties)."
          },
          "objectId": {
            "type": "string",
            "description": "The ID of the object that completed the event (e.g., contact ID or visitor ID)."
          }
        }
      }
    }
  },
  "required": true
}
