http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "options": {
            "type": "array",
            "description": "A list of available options for the property if it is an enumeration. NOTE: This field is only applicable for enumerated properties.",
            "items": {
              "required": [
                "displayOrder",
                "hidden",
                "label",
                "value"
              ],
              "type": "object",
              "properties": {
                "hidden": {
                  "type": "boolean"
                },
                "displayOrder": {
                  "type": "integer",
                  "format": "int32"
                },
                "description": {
                  "type": "string",
                  "description": "null"
                },
                "label": {
                  "type": "string",
                  "description": "null"
                },
                "value": {
                  "type": "string",
                  "description": "null"
                }
              }
            }
          },
          "description": {
            "type": "string",
            "description": "A description of the property that will be shown as help text in HubSpot."
          },
          "label": {
            "type": "string",
            "description": "Human readable label for the property. Used in HubSpot UI"
          }
        }
      }
    }
  },
  "required": true
}
