http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request object that includes the Agent policy to create",
        "properties": {
          "data": {
            "description": "Object for a single Agent rule",
            "properties": {
              "attributes": {
                "description": "Create a new Cloud Workload Security Agent policy",
                "properties": {
                  "description": {
                    "description": "The description of the policy",
                    "example": "My agent policy",
                    "type": "string"
                  },
                  "enabled": {
                    "description": "Whether the policy is enabled",
                    "example": true,
                    "type": "boolean"
                  },
                  "hostTags": {
                    "description": "The host tags defining where this policy is deployed",
                    "items": {
                      "type": "string"
                    },
                    "type": "array"
                  },
                  "hostTagsLists": {
                    "description": "The host tags defining where this policy is deployed, the inner values are linked with AND, the outer values are linked with OR",
                    "items": {
                      "items": {
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "type": "array"
                  },
                  "name": {
                    "description": "The name of the policy",
                    "example": "my_agent_policy",
                    "type": "string"
                  }
                },
                "required": [
                  "name"
                ],
                "type": "object"
              },
              "type": {
                "default": "policy",
                "description": "The type of the resource, must always be `policy`",
                "enum": [
                  "policy"
                ],
                "example": "policy",
                "type": "string",
                "x-enum-varnames": [
                  "POLICY"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "The definition of the new Agent policy",
  "required": true
}
