import { ActorHeader, type ActorHeaderProps } from ".";

const actor = {
  id: "http://abel/actor/AFKb0cQunSBv1fC7sWbQYg",
  avatarUrl: "https://dev.bovine.social/assets/bull-horns.png",
  name: "The kitty",
  identifier: "acct:kitty@abel",
  htmlUrl: "http://abel/@kitty",
};
export default {
  component: ActorHeader,
};

export const FullInformation = {
  render: (args: ActorHeaderProps) => <ActorHeader {...args} />,
  args: {
    actorInfo: actor,
  },
};

export const NoAvatar = {
  render: (args: ActorHeaderProps) => <ActorHeader {...args} />,
  args: {
    actorInfo: { ...actor, avatarUrl: null },
  },
};

export const NoName = {
  render: (args: ActorHeaderProps) => <ActorHeader {...args} />,
  args: {
    actorInfo: { ...actor, name: null },
  },
};

export const CanStyle = {
  render: (args: ActorHeaderProps) => <ActorHeader {...args} />,
  args: {
    actorInfo: { ...actor },
    style: { backgroundColor: "lightgreen" },
  },
};
