import numpy as np
from puzzle_solver import suguru_solver as solver
from puzzle_solver.core.utils import get_pos


def test_ground():
    id_board = np.array([
        ['00', '00', '00', '00', '00', '01', '01', '02', '02', '02', '03', '03', '03', '03', '04'],
        ['00', '05', '05', '06', '06', '06', '01', '02', '02', '03', '03', '07', '07', '04', '04'],
        ['08', '08', '05', '06', '06', '01', '01', '02', '09', '09', '09', '09', '07', '04', '04'],
        ['08', '05', '05', '06', '10', '01', '11', '11', '12', '13', '13', '07', '07', '14', '04'],
        ['08', '05', '15', '16', '10', '10', '17', '17', '12', '12', '13', '07', '14', '14', '18'],
        ['08', '15', '15', '16', '16', '10', '10', '17', '12', '13', '13', '19', '19', '14', '18'],
        ['15', '15', '20', '20', '16', '10', '17', '17', '12', '13', '21', '21', '19', '22', '18'],
        ['15', '23', '23', '20', '16', '16', '17', '24', '24', '24', '21', '19', '19', '22', '18'],
        ['23', '23', '20', '20', '25', '25', '24', '24', '26', '24', '21', '19', '22', '22', '18'],
        ['23', '23', '20', '25', '25', '25', '25', '26', '26', '26', '21', '21', '22', '22', '18']
    ])
    num_board = np.array([
        [' ', '4', ' ', ' ', ' ', ' ', '4', ' ', '3', ' ', ' ', ' ', ' ', ' ', '6'],
        ['6', ' ', '3', ' ', '4', ' ', ' ', ' ', ' ', ' ', '6', ' ', '5', ' ', ' '],
        [' ', ' ', ' ', ' ', ' ', ' ', '5', '6', '2', ' ', ' ', ' ', ' ', ' ', '3'],
        [' ', ' ', '5', ' ', '2', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', '1'],
        ['5', ' ', ' ', ' ', ' ', '6', ' ', ' ', '5', ' ', ' ', '1', ' ', '2', '6'],
        [' ', ' ', ' ', '6', '3', ' ', ' ', ' ', ' ', ' ', ' ', '5', ' ', ' ', ' '],
        [' ', ' ', ' ', ' ', '5', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
        ['2', '6', ' ', '4', ' ', ' ', ' ', '1', ' ', ' ', '2', '4', ' ', '5', ' '],
        ['4', '3', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
        ['2', ' ', ' ', '3', ' ', ' ', '5', '4', ' ', ' ', '1', ' ', ' ', '2', '3'],
    ])
    binst = solver.Board(id_board=id_board, num_board=num_board)
    solutions = binst.solve_and_print()
    # print(np.array([[solutions[0].assignment[get_pos(x=x, y=y)] for x in range(id_board.shape[1])] for y in range(id_board.shape[0])]).astype(str))
    ground = np.array([
        ['2', '4', '5', '1', '3', '2', '4', '2', '3', '4', '5', '4', '3', '2', '6'],
        ['6', '1', '3', '2', '4', '1', '3', '1', '5', '1', '6', '2', '5', '4', '5'],
        ['3', '2', '4', '6', '5', '6', '5', '6', '2', '4', '3', '1', '3', '2', '3'],
        ['1', '6', '5', '3', '2', '1', '2', '1', '3', '6', '5', '4', '6', '4', '1'],
        ['5', '2', '4', '1', '5', '6', '5', '4', '5', '1', '2', '1', '3', '2', '6'],
        ['4', '6', '5', '6', '3', '4', '3', '1', '2', '3', '4', '5', '6', '1', '5'],
        ['3', '1', '3', '1', '5', '1', '2', '6', '4', '1', '6', '3', '2', '3', '4'],
        ['2', '6', '5', '4', '2', '4', '3', '1', '5', '3', '2', '4', '1', '5', '2'],
        ['4', '3', '2', '6', '1', '6', '2', '6', '2', '4', '5', '3', '6', '4', '1'],
        ['2', '1', '5', '3', '2', '4', '5', '4', '1', '3', '1', '4', '1', '2', '3'],
    ])
    ground_assignment = {get_pos(x=x, y=y): int(ground[y][x]) for x in range(ground.shape[1]) for y in range(ground.shape[0])}
    assert set(solutions[0].assignment.keys()) == set(ground_assignment.keys()), f'solution keys != ground assignment keys, {set(solutions[0].assignment.keys()) ^ set(ground_assignment.keys())} \n\n\n{solutions[0].assignment} \n\n\n{ground_assignment}'
    for pos in solutions[0].assignment.keys():
        assert solutions[0].assignment[pos] == ground_assignment[pos], f'solution[{pos}] != ground assignment[{pos}], {solutions[0].assignment[pos]} != {ground_assignment[pos]}'

if __name__ == '__main__':
    test_ground()
